import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { catchError, map, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private apiUrl = 'http://localhost:3000';

  constructor(private http: HttpClient) { }

  login(email: string, password: string): Observable<boolean> {
    return this.http.get<any[]>(`${this.apiUrl}/users`).pipe(
      map((users) => {
        const user = users.find(
          (u: any) => u.email === email && u.password === password
        );
        if (user) {
          localStorage.setItem('currentUser', JSON.stringify(user));
          return true;
        }
        return false;
      })
    );
  }

  logout(): void {
    localStorage.removeItem('currentUser');
  }

  isLoggedIn(): boolean {
    return !!localStorage.getItem('currentUser');
  }

  checkDuplicateEmail(email: string): Observable<boolean> {
    return this.http.get<User[]>(this.apiUrl).pipe(
      map(users => users.some(user => user.email.toLowerCase() === email.toLowerCase())),
      catchError(error => {
        console.error('Error checking duplicate email:', error);
        return [true]; // Fallback to prevent registration on error
      })
    );
  }

  // Register a new user with duplicate email check
  register(user: User): Observable<User> {
    return this.checkDuplicateEmail(user.email).pipe(
      switchMap(isDuplicate => {
        if (isDuplicate) {
          return throwError(() => new Error('This email is already registered. Please use a different email or sign in.'));
        }
        return this.http.post<User>(this.apiUrl, user).pipe(
          map(response => {
            localStorage.setItem('user', JSON.stringify(response)); // Store user on success
            return response;
          })
        );
      }),
      catchError(error => {
        console.error('Registration error:', error);
        return throwError(() => new Error(error.message || 'Registration failed. Please try again.'));
      })
    );
  }
}
