import { Component, OnInit } from '@angular/core';

import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/auth.service';
import { DataService } from 'src/app/core/data.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  searchTerm: string = '';
  filteredProducts: any[] = [];
  products: any[] = [];
  categories: string[] = ['Electronics', 'Clothing', 'Books'];
  selectedCategory: string = '';
  stockAlerts: string[] = [];
  lowStockThreshold: number = 5;
  errorMessage: string | null = null;

  constructor(private dataService: DataService, private authService: AuthService, private router: Router) {}

  ngOnInit() {
    this.loadProducts();
  }

  loadProducts() {
    this.dataService.getProducts().subscribe({
      next: (products) => {
        this.products = products;
        this.applyFilters();
      },
      error: (err) => this.errorMessage = err.message
    });
  }

  applyFilters() {
    let tempProducts = [...this.products];
    if (this.selectedCategory) {
      tempProducts = tempProducts.filter(product => product.category === this.selectedCategory);
    }
    if (this.searchTerm) {
      tempProducts = tempProducts.filter(product =>
        product.name.toLowerCase().includes(this.searchTerm.toLowerCase())
      );
    }
    this.filteredProducts = tempProducts;
    this.checkStockAlerts();
  }

  onSearch() {
    this.applyFilters();
  }

  onCategoryChange(category: string) {
    this.selectedCategory = category;
    this.applyFilters();
  }

  updateQuantity(productId: number, newQuantity: number) {
    const pin = prompt('Enter admin pin (4 digits):');
    if (pin && this.authService.verifyAdminPin(pin)) {
      const updatedProduct = { ...this.products.find(p => p.id === productId), quantity: newQuantity };
      this.dataService.updateProduct(updatedProduct).subscribe({
        next: () => this.loadProducts(),
        error: (err) => this.errorMessage = err.message
      });
    } else {
      alert('Invalid admin pin. Only admins can update.');
    }
  }

  deleteProduct(productId: number) {
    const pin = prompt('Enter admin pin (4 digits):');
    if (pin && this.authService.verifyAdminPin(pin)) {
      this.dataService.deleteProduct(productId).subscribe({
        next: () => this.loadProducts(),
        error: (err) => this.errorMessage = err.message
      });
    } else {
      alert('Invalid admin pin. Only admins can delete.');
    }
  }

  checkStockAlerts() {
    this.stockAlerts = [];
    this.products.forEach(product => {
      if (product.quantity < this.lowStockThreshold) {
        this.stockAlerts.push(`${product.name} is low in stock (${product.quantity} units remaining)`);
      }
    });
    if (this.stockAlerts.length > 0) {
      alert(this.stockAlerts.join('\n'));
    }
  }
}
